(2043, 1, 'Audio & Visual Input (OPV-2BMW) for Mini (i-Drive)', '<newtab>\r\n<tabname><center>Description</center></tabname>\r\n<tabtext><div id="prodDesc">\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>Features</center></tabname>\r\n<tabtext><div id="prodDesc"><p>AV-Input for All Minis with factory fitted navigation with i-Drive and TV Tuner </p><br>\r\n<ul><li>1 AV-Input </li>\r\n<li>1 Reverse Camera Input </li>\r\n<li>Auto Video Switching </li>\r\n<li>1 RGB Input </li>\r\n<li>100% Plug & Play </li>\r\n<li>Video in Motion * </li></ul><br>\r\n<p>Compatible for: All Minis with factory fitted navigation with i-Drive & TV Tuner\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>Support</center></tabname>\r\n<tabtext><div id="prodDesc"><p><a href="http://www.safensound.co.uk/support/nav-tv/OPV-2BMW.pdf" target="_blank"><img src="images/icons/pdf.png" align="left" style="vertical-align:middle"/>Instruction Manual</a>\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>More Images</center></tabname>\r\n<tabtext><div id="prodDesc">\r\n</div>\r\n</tabtext>\r\n</newtab>', '', NULL, '', 755, '<br>\r\n<li>1 AV-Input </li>\r\n<li>1 Reverse Camera Input </li>\r\n<li>Auto Video Switching </li>\r\n<li>1 RGB Input </li>', ''),
(2044, 1, 'Video & Reverse Camera Input (NAV-INBMW) for Mini (i-Bus)', '<newtab>\r\n<tabname><center>Description</center></tabname>\r\n<tabtext><div id="prodDesc">\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>Features</center></tabname>\r\n<tabtext><div id="prodDesc"><p>Video Input for All Minis with factory fitted navigation with i-Bus </p><br>\r\n<p>Video input for integration of aftermarket products (e.g. DVD player) with the factory navigation System </p><br>\r\n<ul><li>1 Video Input </li>\r\n<li>1 Camera Input </li>\r\n<li>Auto Video Switching </li>\r\n<li>PAL & NTSC Compatible </li>\r\n<li>Analogue & Digital Compatible </li>\r\n<li>Plug & Play Connection </li>\r\n<li>Any aftermarket device attached to this interface will work on the move</li></ul><br>\r\n<p>Compatible with: All Minis with factory fitted navigation with i-Bus\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>Support</center></tabname>\r\n<tabtext><div id="prodDesc"><p><a href="http://www.safensound.co.uk/support/nav-tv/NAV-INBMW.pdf" target="_blank"><img src="images/icons/pdf.png" align="left" style="vertical-align:middle"/>Instruction Manual</a>\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>More Images</center></tabname>\r\n<tabtext><div id="prodDesc">\r\n</div>\r\n</tabtext>\r\n</newtab>', '', NULL, '', 867, '<br>\r\n<li>1 Video Input </li>\r\n<li>1 Camera Input </li>\r\n<li>Auto Video Switching </li>\r\n<li>PAL & NTSC Compatible </li>', ''),
(2045, 1, 'BMW Z4 2003> E85 Fascia, iso & aerial adapter', '<newtab>\r\n<tabname><center>Description</center></tabname>\r\n<tabtext><div id="prodDesc"><p>This Full kit will allow installation of an aftermarket stereo into a BMW Z4 2003 onwards</p><br>\r\n<p>It will not operate any steering controls or work with any factory installed amplifiers.</p><br>\r\n<p>The kit is actually made up by us actually installing an aftermarket radio in the listed vehicle. The factory stereo in the vehicle we worked on had a BMW Business CD as standard.\r\n</p><br>\r\n<p>All the products we sell are from either Connects2, Autoleads or Celsus and of course are good quality and brand new.</p><br>\r\n<p>Whilst we endeavour to supply you with the correct lead with the descriptions we have no doubt there will be some vehicles caught in change over years so it would be useful when purchasing to match the picture with the type of fascia or loom of your vehicle prior to purchasing.</p>\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>Vehicles</center></tabname>\r\n<tabtext><div id="prodDesc"><p><b>Suitable For:</b></p><br>\r\n<ul><li>BMW Z4 E83</li></ul>\r\n<p><b>This does not fit cars with factory amplified sound system.</b></p>\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>Support</center></tabname>\r\n<tabtext><div id="prodDesc"><p>If you experience any difficulties please do not hesitate to contact us for any technical enquires you may have. <a href="mailto:technical@safensound.co.uk">technical@safensound.co.uk</a></p>\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>More Images</center></tabname>\r\n<tabtext><div id="prodDesc"><p></p>\r\n</div>\r\n</tabtext>\r\n</newtab>', '', NULL, '', 1776, '<br>\r\n<li>BMW Z4 E85 2003> fitting kit</li>', ''),
