(2145, 1, 'Audison: SFD41C - Fuse & Distribution Block 4-Way', '<newtab>\r\n<tabname><center>Description</center></tabname>\r\n<tabtext><div id="prodDesc">\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>Features</center></tabname>\r\n<tabtext><div id="prodDesc"><ul><li>Fuse & Distribution block, FOUR WAY</li>\r\n<li>UNIQUE ONE PIECE fuse and ground system for clean, reliable installations</li>\r\n<li>POLYCARBONATE casing, strong and scratch resistant</li>\r\n<li>1/0 AWG on both fused and ground input, 1/0 AWG pass thru on fused block</li>\r\n<li>SMBTM solid machined brass construction for better current transfer</li>\r\n<li>PLATINUM SATIN finish for unparalleled cosmetic appeal</li>\r\n<li>KNURLED SCREWS for better contact with the attached wire</li>\r\n<li>RECESSED inset cable collar for protection against stray wire strands</li>\r\n<li>MOLDED CABLE REST keeps wires safe and nicely laid out</li>\r\n<li>1  2 AWG and 3  4 AWG on both AFS fused and ground outputs.</li></ul>\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>Support</center></tabname>\r\n<tabtext><div id="prodDesc">\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>More Images</center></tabname>\r\n<tabtext><div id="prodDesc">\r\n</div>\r\n</tabtext>\r\n</newtab>', '', NULL, '', 2573, '<br>\r\n<li>Fuse & Distribution block, FOUR WAY</li>\r\n<li>KNURLED SCREWS for better contact </li>\r\n<li>MOLDED CABLE REST keeps wires safe</li>', ''),
(2146, 1, 'Connection by Audison: ST2-500 - 5m RCA', '<newtab>\r\n<tabname><center>Description</center></tabname>\r\n<tabtext><div id="prodDesc">\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>Features</center></tabname>\r\n<tabtext><div id="prodDesc"><ul><li>5 metre SONUS-RANGE RCA lead</li>\r\n<li>"ST SERIES HIGH RESOLUTION AUDIOPHILE SIGNAL CABLES</li>\r\n<li>DURA-TEX outer jacket for protection and to enhance the feel and flexibility</li>\r\n<li>SOFT TOUCH outer jacket makes for an extremely flexible cable</li>\r\n<li>110% TWISTED MYLAR AND ALUMINUM shield rejects RFI and EMI</li>\r\n<li>TEXTILE COTTON INTERNAL FIBERS protect silver finished conductors</li>\r\n<li>DDW Double Drain Wire system to drain electrostatic fields. No noise</li>\r\n<li>WTP Winding Twisted Pair balanced conductors reject low frequency electromagnetic fields aiding in noise rejection</li>\r\n<li>OVER 80db noise rejection</li>\r\n<li>PLATINUM SATIN machined ABS style connector for a strong long lasting connection</li>\r\n<li>TUG-PROOF sealed internal connection for maximum reliability</li>\r\n<li>DIE-CAST Y BLOCK for full protection of the conductors in critical split point</li>\r\n<li>REFERENCE GRADE performance</li></ul>\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>Support</center></tabname>\r\n<tabtext><div id="prodDesc">\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>More Images</center></tabname>\r\n<tabtext><div id="prodDesc">\r\n</div>\r\n</tabtext>\r\n</newtab>', '', NULL, '', 2009, '<br>\r\n<li>5 metre SONUS-RANGE RCA lead</li>\r\n<li>OVER 80db noise rejection</li>\r\n<li>REFERENCE GRADE performance</li', ''),
(2147, 1, 'Stinger SPV20 Battery', '<newtab>\r\n<tabname><center>Description</center></tabname>\r\n<tabtext><div id="prodDesc"><p>Stingers POWER Series batteries offer many of the same technologies as our remarkable Power2 series at an outstanding value. The valve regulated, sealed lead acid battery utilizes dry cell technology with an absorbed glass mat for performance that comes just short of the POWER2 but far ahead of other batteries! This makes the POWER series the perfect choice for excellent Dry Cell performance on a budget.</p><br>\r\n<p>Stingers POWER Series batteries offer many of the same technologies as our remarkable Power2 series at an outstanding value. The valve regulated, sealed lead acid battery utilizes dry cell technology with an absorbed glass mat for performance that comes just short of the POWER2 but far ahead of other batteries! This makes the POWER series the perfect choice for excellent Dry Cell performance on a budget.</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>Features</center></tabname>\r\n<tabtext><div id="prodDesc"><ul><li>Internal resistance: <5 mOhms</li>\r\n<li>5 Second Cranking: 300A</li>\r\n<li>Weight: 18.00 lbs(Approx)</li>\r\n<li>Temperature range: -15 to +50 C </li>\r\n<li>Dimensions: 7-1/2" H x 7-1/8" W x 3" D</li>\r\n<li>One year warranty</li>\r\n<li>Zero-maintenance, non-spillable SPV20 makes for worry-free installations where size is an issue!</li></ul>\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>Support</center></tabname>\r\n<tabtext><div id="prodDesc">\r\n</div>\r\n</tabtext>\r\n</newtab>\r\n\r\n<newtab>\r\n<tabname><center>More Images</center></tabname>\r\n<tabtext><div id="prodDesc">\r\n</div>\r\n</tabtext>\r\n</newtab>', '', NULL, '', 628, '<br>\r\n<li>Internal resistance: <5 mOhms</li>\r\n<li>5 Second Cranking: 300A</li>\r\n<li>Weight: 18.00 lbs(Approx)</li>\r\n<li>Temperature range: -15 to +50 C </li>', ''),
